sp_widget.client_script:
function($scope , spModal, spNavStateManager, spUtil) {
  /* widget controller */
  var c = this;


	$scope.batchFill = function(){

		var shared = {};


		spModal.open({
			size: "lg",
			title: "Batch Fill Brucellosis Market",
			widget: '9098e149db2197409b2689584b9619bf',
			shared: c.data,
			buttons: [
					{label: "Submit",
					primary: true}
			]

		}).then(function(){

			c.server.update();
		});

		}

	$scope.deleteTheLine = function(table , item) {

			c.data.toBeDeleted = item.sys_id;

			console.log("button was pressed" + " " +table + " " + item.sys_id);
			c.data.deleteLine = true;
			c.server.update();



		}


		$scope.newRecord = function(){

		var widgetId = 'widget-form';
		var widgetInput = {
			table: $scope.data.table,
			view: $scope.data.view,
			sys_id: '-1'
		};

		spModal.open({
			title: 'Test Results',
			widget: widgetId,
			size: 'lg',
			widgetInput: widgetInput,
			buttons: [
				{
					label: '${OK}',
					primary: true
				}
			]
		}).then(function () {
			//console.log('it did something at least');
			c.server.update();

		});
	};


	$scope.go = function(table , item){
		var widgetId = 'widget-form';
		var widgetInput = {
			table: $scope.data.table,
			view: $scope.data.view,
			sys_id: item.sys_id

		};



		spModal.open({
			title: "Lab Result",
			widget: widgetId,
			size: 'md',
			widgetInput: widgetInput,
			buttons: [
				{
					label: '${OK}',
					primary: true
				}
			]

		}).then(function(){
			spUtil.update($scope);

		});

	}

	c.update = function update() {
			spUtil.update($scope);
	}

	console.log(c.data.table);


		spUtil.recordWatch($scope, c.data.table, c.data.filter, function(name, data){
			spUtil.update($scope);
		});
		console.log("i'm watching you: " + c.data.table + " with a filter of: " + c.data.filter);

}

