sys_script_include.script:
var u_UserFields = Class.create();
u_UserFields.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


	getUserAddress : function() { //Returns the street of the currently logged in user.
	var gr = new GlideRecord('sys_user');

	gr.get(gs.getUserID());

	return gr.street;
},

	getCityStateZip : function() { // Returns the City, State and Zip of the currently logged in user
		var gr = new GlideRecord('sys_user');
		gr.get(gs.getUserID());

		return gr.city + ", " + gr.state + ", " + gr.zip;
	},

	getPhone : function() {  //Returns the phone number of the currently logged in user
		var gr = new GlideRecord('sys_user');
		gr.get(gs.getUserID());

		return gr.phone;
	},

    type: 'u_UserFields'
});
