sys_script_include.script:
var ISDAScripts = Class.create();
ISDAScripts.prototype = {
    initialize: function() {
    },

		generateID : function(prefix, table){

		var result = '';
		var tablename = table;
		var number = current.number.substring(3);
		var sessionID = gs.getSessionID();
		var uniqueformid =  prefix + number  + sessionID;
		gs.addInfoMessage(uniqueformid);
		var gr = new GlideRecord(tablename);
		gr.addEncodedQuery("unique_form_id=" + uniqueformid);
		gr.query();
		 gs.addInfoMessage(gr.getRowCount());
		if (gr.next()){

			 return gr.unique_form_id;	
		}
		else

		return uniqueformid;
},

    type: 'ISDAScripts'
};
